mapper.select:
SELECT * from 
(
	SELECT
		id,tmbh,tmzl,tmlx,tmnr,tmxx,tmda,tmfz,tmnyd,createtime
	FROM
		b_tmxx
	WHERE
		tmzl = #{tmzl}
		AND tmlx = #{tmlx}
		AND tmnyd = #{tmnyd}
		AND delFlag = 0
		ORDER BY RAND() LIMIT #{tmsl}
) tmxx
ORDER BY tmxx.tmlx,tmxx.tmzl,tmxx.createtime

mapper.update:
UPDATE
	b_xycj
SET kscj = #{kscj}
WHERE
	xybh = #{xybh}
	AND ksbh = #{ksbh}

UPDATE
	b_xycj
SET isExamFlag = '1'
WHERE
	xybh = #{xybh}
	AND ksbh = #{ksbh}
